mapper.select:
select
id,
user_id as userId,
dialog_type as dialogType,
region,
`number`,
remark,
`time`,
`project_name` as projectName
YEAR(`time`) as `year`,
MONTH(`time`) as `month`,
DAY(`time`) as `day`
from record

    user_id = #{userId}

        and dialog_type = #{dialogType}


        and region = #{region}


        and YEAR(`time`) = #{year} and MONTH(`time`) = #{month} and DAY(`time`) = #{day}


        and YEAR(`time`) = #{year} and MONTH(`time`) = #{month}


        and YEAR(`time`) = #{year}

select id, user_id as userId, dialog_type as dialogType, region, `number`, remark, `time`, project_name as projectName from record
user_id = #{userId}
and dialog_type = #{dialogType}
and region = #{region}
